 */

@import './utils/variables';

@import './base/common';
@import './base/section';
@import './base/container';

@import './header';
@import './header-menu';
@import './header-modal';
@import './products';
@import './about';
@import './advantages';
@import './gallery';
@import './opinions';
@import './location';
@import './footer';

