.header__logo {
  width: 199px;
  height: 50px;
  mix-blend-mode: multiply;
  cursor: pointer;
}

.header__bottom {
  position: relative;
}

.header__title {
  position: absolute;
  font-family: var(--txt-font);
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: var(--txt-color);
  z-index: 3;
  font-weight: 400;
}

.header__title--span {
  color: var(--dark-pink);
  text-shadow: 2px 6px 2px rgba(255, 165, 186, 1);
}

.header__buttons {
  position: absolute;
  z-index: 3;
}

.header__btn,
.header__buttons--link {
  font-family: var(--contact-font);
  align-items: center;
  text-align: center;
  justify-content: center;
  color: var(--dark-pink);
  background-color: var(--txt-color);
  border: none;
  border-radius: 20px;
  cursor: pointer;
  -webkit-box-shadow: 6px 13px 23px -11px rgba(212, 20, 67, 1);
    -moz-box-shadow: 6px 13px 23px -11px rgba(212, 20, 67, 1);
    box-shadow: 6px 13px 23px -11px rgba(212, 20, 67, 1);
}

.header__btn {
  width: 100%;
  height: 100%;
}

.header__buttons--link:hover,
.header__buttons--link:focus,
.header__btn:hover,
.header__btn:focus {
  color: var(--txt-color);
  background-color: var(--dark-pink);
}

.link {
  text-decoration: none;
}

